//gets, validates, & defines user input
void second_form::set_player_input(QString raw)
{
    //used for parsed user input
    QString x;
    QString y;
    bool a;
    bool b;
    a = false;
    b = false;
    //parse user input into two individual characters
    x = raw.at(0);
    y = raw.at(1);

    //compares input to data in arrays
    for (int i=0; i<10; i++){
        //if the first character from user input is a-j
        if (x == get_qLetters(i)){
            set_letter_side(i);
            //first character is valid
            a = true;
        }
        //if the second character from user input is 0-1
        if (y == get_qNumbers(i)){
            set_number_side(i);
            //second character is valid
            b = true;
        }
    }

    //if user input is valid (a-j & 0-9)
    if (a == true && b == true){ set_valid(true);}
    if (a != true || b == true){ set_valid(false);}
    if (a == true || b != true){ set_valid(false);}
    if (a != true && b != true){ set_valid(false);}
}
