void second_form::set_difficulty(QString rawInput){
    if (rawInput == "H" || rawInput == "h"){
        set_numOfTrys(30);
        set_tryCount(0);
        ui->trysLabel->setText("ATTEMPT COUNTER");
        ui->tryCountLabel->setText("0");
        ui->outOfLabel->setText("out of 30");
        ui->command_line->setText("");
        set_valid(true);
    }
    if (rawInput == "M" || rawInput == "m"){
        set_numOfTrys(60);
        ui->trysLabel->setText("ATTEMPT COUNTER");
        ui->tryCountLabel->setText("0");
        ui->outOfLabel->setText("out of 60");
        set_valid(true);
    }
    if (rawInput == "E" || rawInput == "e"){
        set_numOfTrys(100);
        ui->trysLabel->setText("ATTEMPT COUNTER");
        ui->tryCountLabel->setText("0");
        ui->outOfLabel->setText("out of 100");
        set_valid(true);
    }
}

